mapper.select:
select

  distinct


from ana_bms_season_charge




  order by ${orderByClause}



    limit ${offset}, ${limit}


    limit ${limit}

select 

from ana_bms_season_charge
where bms_code = #{bmsCode,jdbcType=VARCHAR}
  and operator_id = #{operatorId,jdbcType=VARCHAR}
  and connector_id = #{connectorId,jdbcType=VARCHAR}
  and statistical_season = #{statisticalSeason,jdbcType=CHAR}

mapper.delete:
delete from ana_bms_season_charge
where bms_code = #{bmsCode,jdbcType=VARCHAR}
  and operator_id = #{operatorId,jdbcType=VARCHAR}
  and connector_id = #{connectorId,jdbcType=VARCHAR}
  and statistical_season = #{statisticalSeason,jdbcType=CHAR}

mapper.insert:
insert into ana_bms_season_charge (bms_code, operator_id, connector_id, 
  statistical_season, station_id, equipment_id, 
  station_street, bms_ver, statistics_times
  )
values (#{bmsCode,jdbcType=VARCHAR}, #{operatorId,jdbcType=VARCHAR}, #{connectorId,jdbcType=VARCHAR}, 
  #{statisticalSeason,jdbcType=CHAR}, #{stationId,jdbcType=VARCHAR}, #{equipmentId,jdbcType=VARCHAR}, 
  #{stationStreet,jdbcType=VARCHAR}, #{bmsVer,jdbcType=VARCHAR}, #{statisticsTimes,jdbcType=INTEGER}
  )

mapper.update:
