mapper.select:
  	SELECT
			bms_code,
			operator_id,
			station_id,
			equipment_id,
			connector_id,
			startTime,
			endTime
		FROM
			ana_bms_single_charge
		WHERE

        		DATE_FORMAT(endTime, '%Y-%m-%d %H') = #{param.startStatisticalHour}


        		statistical_date = #{param.startStatisticalDay}


        		statistical_week = #{param.startStatisticalWeek}


        		statistical_month = #{param.startStatisticalMonth}


        		statistical_year = #{param.startStatisticalYear}

        AND bMS_code = #{param.bMSCode,jdbcType=VARCHAR} AND operator_id = #{param.operatorId,jdbcType=VARCHAR} AND connector_id = #{param.connectorId,jdbcType=VARCHAR}
